(()=>{var a=document.querySelector(".collection__toolbar__filter"),r=document.querySelectorAll(".collection__toolbar-filter-btn");r.forEach(e=>{e.addEventListener("click",()=>{let t=e.getAttribute("data-state");a.classList.toggle("active"),!t||t==="closed"?(e.setAttribute("data-state","opened"),e.setAttribute("aria-expanded","true")):(e.setAttribute("data-state","closed"),e.setAttribute("aria-expanded","false"))})});})();
