(()=>{window.handleChangeSortBy=s=>{let t=document.getElementById("sort_by").dataset.url,l=document.getElementById("form_sort_by").dataset.sectionId,o=document.querySelector("#collection__products"),d=s.target.value,i=t+"?sort_by="+d+"&section_id="+l+"";fetch(i).then(e=>e.text()).then(e=>{let n=document.createElement("div");n.innerHTML=e;let c=n.querySelectorAll("#collection__products > *");o.innerHTML="",c.forEach(a=>{o.appendChild(a)})})};window.handleChangeShow=s=>{let u=document.getElementById("form_show"),t=document.querySelector("#collection__products"),r=u.dataset.sectionId,l=s.target.value,o=`${window.Shopify.routes.root}cart/update.js`,d={attributes:{IdCart:l},sections:[r]},i={method:"POST",body:JSON.stringify(d),headers:{"Content-Type":"application/json"}};fetch(o,i).then(e=>e.json()).then(e=>{let n=e.sections[r],c=document.createElement("div");c.innerHTML=n;let a=c.querySelectorAll("#collection__products > *");t.innerHTML="",a.forEach(m=>{t.appendChild(m)})}).catch(e=>console.error(e))};})();
